const fetchData = async (file) => {
  const response = await fetch(`/${file}.json`);  // Ruta al archivo JSON en la carpeta public
  if (response.ok) {
    return response.json();
  } else {
    throw new Error('Failed to fetch data');
  }
};
const listaVideos = async () => {
  try {
    const data = await fetchData();
    return data.videos;
  } catch (error) {

    throw new Error('Error fetching video data');
  }
};

const cadastraVideo = async (titulo, url, img, categoria, descricao, codigo) => {
  try {
    const data = await fetchData('data');
    const newVideo = {
      id: data.videos.length + 1,
      titulo: titulo,
      url: url,
      img: img,
      categoria: categoria,
      descricao: descricao,
      codigo: codigo
    };
    data.videos.push(newVideo);
    // Aquí deberías escribir el archivo actualizado de vuelta al sistema de archivos
    return newVideo;
  } catch (error) {

    throw new Error('Não foi possível cadastrar um video.');
  }
};

const video = async (id) => {
  try {
    const data = await fetchData('data');
    const foundVideo = data.videos.find(video => video.id === id);
    if (foundVideo) {
      return foundVideo;
    }
    throw new Error('Não foi possivel encontrar Video.');
  } catch (error) {

    throw new Error('Não foi possivel encontrar Video.');
  }
};

const listaCategorias = async () => {
  try {
    const data = await fetchData('categoria');
    return data.categorias;
  } catch (error) {

    throw new Error('Error fetching categories');
  }
};

const cadastraCategoria = async (nome, descricao, cor, codigo) => {
  try {
    const data = await fetchData('categoria');
    const newCategoria = {
      id: data.categorias.length + 1,
      nome: nome,
      descricao: descricao,
      cor: cor,
      codigo: codigo
    };
    data.categorias.push(newCategoria);
    // Aquí deberías escribir el archivo actualizado de vuelta al sistema de archivos
    return newCategoria;
  } catch (error) {

    throw new Error('Não foi possível cadastrar uma Categoria.');
  }
};

const removeCategoria = async (id) => {
  try {
    const data = await fetchData('categoria');
    const updatedCategorias = data.categorias.filter(categoria => categoria.id !== id);
    if (updatedCategorias.length === data.categorias.length) {
      throw new Error('Não foi possível remover a categoria.');
    }
    data.categorias = updatedCategorias;
    // Aquí deberías escribir el archivo actualizado de vuelta al sistema de archivos
  } catch (error) {

    throw new Error('Não foi possível remover a categoria.');
  }
};

const editaCategoria = async (id, nome, descricao, cor, codigo) => {
  try {
    const data = await fetchData('categoria');
    const categoriaIndex = data.categorias.findIndex(categoria => categoria.id === id);
    if (categoriaIndex === -1) {
      throw new Error('Não foi possível editar categoria.');
    }
    data.categorias[categoriaIndex] = {
      id: id,
      nome: nome,
      descricao: descricao,
      cor: cor,
      codigo: codigo
    };
    // Aquí deberías escribir el archivo actualizado de vuelta al sistema de archivos
    return data.categorias[categoriaIndex];
  } catch (error) {

    throw new Error('Não foi possível editar categoria.');
  }
};
export const videosService = {
  listaVideos,
  cadastraVideo,
  listaCategorias,
  cadastraCategoria,
  removeCategoria,
  editaCategoria,
  video
};
